create database projeto;

create table TipoUsuario(

id int primary key auto_increment,
nome varchar(50) not null

);

create table Usuario(

id int primary key auto_increment,
nome varchar(100) not null,
email varchar(50) not null unique,
senha varchar(100) not null,
tipoUsuarioFk int,

foreign key (tipoUsuarioFK) references TipoUsuario(id)
);

create table Disciplina(

id int primary key auto_increment,
nome varchar(100) not null
);

create table Nivel(

id int primary key auto_increment,
nome varchar(100) not null
);


create table Perguntas(

id int primary key auto_increment,
descricao varchar(1000) not null,
idNivelFK int,
idDisciplinaFK int,

foreign key (idNivelFK) references Nivel(id)
 ON DELETE CASCADE,
foreign key (idDisciplinaFK) references Disciplina(id)
 ON DELETE CASCADE
);

delete from Perguntas where id = 1;

create table Alternativa(

id int primary key auto_increment,
descricao varchar(500) not null,
alterCorreta varchar(200) not null,
idPerguntaFK int,

foreign	key (idPerguntaFK) references Perguntas(id)
 ON DELETE CASCADE

);

create table Resposta(

id int primary key auto_increment,
idPerguntasFK int,
idUsuarioFK int,
correto varchar (1000),


 foreign key (idPerguntasFK) references  Perguntas(id)
 ON DELETE CASCADE,
 foreign key (idUsuarioFK) references Usuario(id)
 ON DELETE CASCADE

);

